var app = (function() {
    var setItem = function(key, value) {
        return localStorage.setItem(key, JSON.stringify(value));
    };

    var getItem = function(key) {
        return JSON.parse(localStorage.getItem(key)) || [];
    };

    var tasks = getItem('tasks') || [];

    var id = function(){
        return tasks.length;
    };

    return {
        tasks : tasks,
        id : id,
        setItem : setItem,
        getItem : getItem
    };
})();


/*** Created by sidhu on 11/6/14. */
var CUI =(function() {
    var $summary = $('#summary'),
        $priority = $('#priority'),
        $dueDate = $('#dueDate'),
        $description = $('#description'),
        $originalEstimate = $('#originalEstimate');

        var getInputs  = function() {
            console.log($summary.val());
            var data = {};
                data.id = app.id();
                data.summary =  $summary.val();
                data.priority = $priority.val();
                data.dueDate = $dueDate.val();
                data.originalTime =$originalEstimate.val();
                data.description = $description.val() || 'no description available';
                $('#task input, textarea, select').val('');
            return data;
        };

        var createTask = function(e) {
            e.preventDefault();
            var values = getInputs();
            if(values.summary) {
                app.tasks.push(values);
                app.setItem('tasks', app.tasks);
            }
        };
        return {
          create : createTask
        };
})();

$(function() {
    $('#createBtn').on('click',CUI.create);
});
/**
 * Created by sidhu on 20/6/14.
 */
var list = (function() {
    var template = Handlebars.compile($('#list-template').html());
    var renderList = function(val) {

        $('#panel').html(template(val));
    };
    return {
        render : renderList
    };

})();

$(function() {
   list.render(app.tasks);

    $('#taskList').on('click','a', function() {
       var id = parseInt($(this).attr('id'));
       var current = _.find(app.tasks, function(i) {
               return i.id === id;
           });
       app.setItem('updateValue',current);

    });


    $('#search').on('keyup', function() {
        var val = $(this).val(),
        filterData = _.filter(app.tasks, function(i){
            if( _.contains(i.summary, val) || _.contains(i.description, val))
              return i;
        });
        list.render(filterData);
    });
});
/**
 * Created by sidhu on 20/6/14.
 */
var UI = (function() {
    var $summary = $('#summary'),
        $priority = $('#priority'),
        $dueDate = $('#dueDate'),
        $description = $('#description'),
        $originalEstimate = $('#originalEstimate'),
        data = {},
        update = app.getItem('updateValue');

    var init = function() {
        $summary.val(update.summary);
        $priority.val(update.priority);
        $dueDate.val(update.dueDate);
        $originalEstimate.val(update.originalTime);
        $description.val(update.description);
    };
    var updateTask = function(e) {
        e.preventDefault();
        data.id = update.id;
        data.summary =  $summary.val(),
        data.priority =  $priority.val(),
        data.dueDate =  $dueDate.val(),
        data.originalTime =  $originalEstimate.val(),
        data.description =  $description.val();
        if(data.summary) {
            app.tasks[data.id] = data;
            app.setItem('tasks', app.tasks);
        }
        $('#task input, textarea, select').val('');
        app.setItem('updateValue',[]);
        window.location.href = 'index.html';
    };
    return {
        init : init ,
        update :updateTask
    };
})();

$(function() {
    UI.init();
    $('#updateBtn').on('click', UI.update);
});